
<style id="5play-custom-style">@media (max-width:639px){.report-post-link{margin-left:0!important}}.ads-here{position:relative;display:flex;align-items:stretch;flex-wrap:wrap;min-height:150px;padding:15px;border:1px solid rgba(0,0,0,.05);border-radius:3px;color:#989b9f;font-size:75%}.ads-here:after{content:'';display:block;width:50px;height:10px;background-color:rgba(0,0,0,.05);border-radius:0 0 0 8px;position:absolute;top:0;right:0}.ads-here .ads-img{width:45%;display:flex;flex-shrink:0;align-items:center;justify-content:center;font-style:normal;background-color:rgba(0,0,0,.05)}.ads-here .ads-img:before{content:'Sample ad';color:var(--colorsvg)}.ads-here .ads-content{width:55%;padding:20px 0 0 20px}.ads-here .ads-content:before,.ads-here .ads-content:after{content:'';display:block;width:100%;padding-top:4%;background-color:rgba(0,0,0,.05)}.ads-here .ads-content:after{width:70%;margin-top:12px}.ads-here.feed .ads-img{width:100%;min-height:90px;margin-bottom:15px}.ads-here.feed .ads-content{padding:0;width:60%}.ads-here.feed .ads-content:before,.ads-here.feed .ads-content:after{padding-top:6%}@media screen and (max-width:768px){.ads-here{min-height:90px}.ads-here .ads-content{padding-top:8px}.ads-here .ads-content:before,.ads-here .ads-content:after{padding-top:8%}}.dmca-sample{font-size:8px;color:rgba(254,254,254,.6);border-radius:3px;overflow:hidden}.dmca-sample:before{content:'DMCA';padding:5px 10px;background-color:#2a2a2a}.dmca-sample:after{content:'Protected';padding:5px 10px;background-color:rgba(0,0,0,.1)}.page-form-left .title,.page-head-main > .title,.log-in,.f-right,.entry-app .genre,.read-more a,.entry-coms .title a,.entry-coms-reply,.copyright a,.upper,.ads-here,.c-green,.specs-list a,.likes > a > .like-plus,.tab-item.active,.menu-butter,.bbcodes,.ui-button,.qq-upload-button,a.back_to_main:hover{color:var(--main-heading)!important}.s-green,.s-yellow,.s-blue,.ads-here .ads-img,.ads-here::after,.ads-here .ads-content::before,.ads-here .ads-content::after,.speedbar-panel,.rating_progress_bar > span,.page_prev > a,.page_next > a,.page-head-cat,.link-faq::after{background-color:var(--main-heading)!important}.s-green,.s-yellow,.s-blue,.pages-list span:not(.nav_ext),.speedbar-panel,.page_prev > a,.page_next > a,.page-head-cat{background-image:linear-gradient(180deg,var(--main-heading) 0%,var(--main-heading) 100% )}.pages-list a:hover,.link-faq:hover,.menu-toggle.menu-close.bbcodes{background-color:var(--rgbacolorbackground)!important}.pages-list a,.link-faq:hover{color:var(--main-heading)!important}.speedbar-panel{text-transform:capitalize!important}.label-updates{color:var(--colorsvg)}.label-updates::before{box-shadow:0 .25rem .5rem 0 rgba(55,176,229,.3)}.label-new{color:var(--main-heading)}.label-new::before{box-shadow:0 .25rem .5rem 0 rgba(249,86,61,.3)}@media (max-width:767px){.pages-list span:not(.nav_ext){color:var(--rgbacolorbackground)!important}#dle-comments-form{margin-left:20px;margin-right:20px}}.kk-star-ratings .kksr-legend{margin-left:.85em!important;margin-right:.85em!important;font-size:12px!important;opacity:.5!important;line-height:.85!important}.kksr-icon{width:14px!important;height:14px!important}.entry-coms .title a,.entry-coms-reply{color:var(--main-heading)!important}.log-in{background:var(--rgbacolorbackground)!important;color:var(--main-heading)!important;transition:background .2s ease}.remodal-cancel,.remodal-confirm{color:white!important;background:var(--main-heading)!important}#report-post-modal-title{color:var(--main-heading)!important}</style>